import { IgrLegendBase } from "./igr-legend-base";
import { ScaleLegend } from "./ScaleLegend";
/**
 * Represents a legend that indicates the size and the color scale for a collection of series.
*/
export class IgrScaleLegend extends IgrLegendBase {
    createImplementation() {
        return new ScaleLegend();
    }
    /**
                                 * @hidden
                                 */
    get i() {
        return this._implementation;
    }
    constructor(props) {
        super(props);
    }
}
