import { IgrSelectedItemChangedEventArgs } from "./igr-selected-item-changed-event-args";
import { ensureBool } from "igniteui-react-core";
/**
 * Provides data for the SelectedItemChanging event.
*/
export class IgrSelectedItemChangingEventArgs extends IgrSelectedItemChangedEventArgs {
    /**
                                 * @hidden
                                 */
    get i() {
        return this._implementation;
    }
    constructor() {
        super();
    }
    /**
     * Set this to true in order to stop the SelectedItem from changing.
    */
    get cancel() {
        return this.i.cancel;
    }
    set cancel(v) {
        this.i.cancel = ensureBool(v);
    }
}
