import { IgrSelectedItemsChangedEventArgs } from "./igr-selected-items-changed-event-args";
import { ensureBool } from "igniteui-react-core";
/**
 * Provides data for the SelectedItemsChanging event.
*/
export class IgrSelectedItemsChangingEventArgs extends IgrSelectedItemsChangedEventArgs {
    /**
                                 * @hidden
                                 */
    get i() {
        return this._implementation;
    }
    constructor() {
        super();
    }
    /**
     * Set this to true in order to stop the SelectedItems collection from changing.
    */
    get cancel() {
        return this.i.cancel;
    }
    set cancel(v) {
        this.i.cancel = ensureBool(v);
    }
}
