import { IgrHorizontalStackedSeriesBase } from "./igr-horizontal-stacked-series-base";
import { StackedAreaSeries } from "./StackedAreaSeries";
/**
 * Represents a IgxDataChartComponent stacked area series.
*/
export class IgrStackedAreaSeries extends IgrHorizontalStackedSeriesBase {
    createImplementation() {
        return new StackedAreaSeries();
    }
    /**
                                 * @hidden
                                 */
    get i() {
        return this._implementation;
    }
    constructor(props) {
        super(props);
    }
    /**
     * Gets whether the current series shows an area shape.
    */
    get isArea() {
        return this.i.eu;
    }
}
