import { IgrHorizontalStackedSeriesBase } from "./igr-horizontal-stacked-series-base";
import { StackedLineSeries } from "./StackedLineSeries";
/**
 * Represents a stacked series, where the points in each series are connected with a line.
*/
export class IgrStackedLineSeries extends IgrHorizontalStackedSeriesBase {
    createImplementation() {
        return new StackedLineSeries();
    }
    /**
                                 * @hidden
                                 */
    get i() {
        return this._implementation;
    }
    constructor(props) {
        super(props);
    }
    /**
     * Gets whether the current series shows only line shapes.
    */
    get isLineOnly() {
        return this.i.fk;
    }
}
